import React from 'react';

export interface AppState{
  amount: number, 
  oneRupee: number,
  twoRupee:number ,
  fiveRupee:number, 
  tenRupee:number,
  twentyRupee: number,
  fiftyRupee: number,
  hundredRupee:number,
  twohundredRupee: number,
  fivehundredRupee:number,
  thousandRupee: number
}

export interface AppProps{

}

class App extends React.Component<AppProps,AppState> {
  constructor(props: AppProps){
    super(props)
    this.state ={
      amount: 0, 
      oneRupee: 0,
      twoRupee:0 ,
      fiveRupee:0, 
      tenRupee:0,
      twentyRupee: 0,
      fiftyRupee: 0,
      hundredRupee:0,
      twohundredRupee: 0,
      fivehundredRupee:0,
      thousandRupee: 0
    }
    this.handleChange = this.handleChange.bind(this);
    this.calculateNotes = this.calculateNotes.bind(this);
  }

  handleChange(e: any){
    this.setState({amount: e.target.value})
  }

  calculateNotes(){
    const amount = this.state.amount;
    let remainingValue = amount

    if(remainingValue >= 2000){
      var twoThousandNotes = Math.floor(amount/2000)
      // this.setState({thousandRupee : Math.floor(amount/2000)})
      remainingValue = amount % 2000
    }

    if(remainingValue >= 500){
      var fiveHundredNotes = Math.floor(remainingValue/500)
      // this.setState({fivehundredRupee: Math.floor(remainingValue/500)})
      remainingValue = remainingValue%500
    }

    if(remainingValue >= 200){
      var twoHundredNotes = Math.floor(remainingValue/200)
      // this.setState({twohundredRupee: Math.floor(remainingValue/200)});
      remainingValue = remainingValue%200
    }

    if(remainingValue >= 100){
      var hundredNotes = Math.floor(remainingValue/100)
      // this.setState({hundredRupee: Math.floor(remainingValue/100)})
      remainingValue = remainingValue%100
    }

    if(remainingValue >= 50){
      var fiftyNotes = Math.floor(remainingValue/50)
      // this.setState({fiftyRupee: Math.floor(remainingValue/50)})
      remainingValue = remainingValue%50
    }
    if(remainingValue >= 20){
      var twentyRupeesNotes = Math.floor(remainingValue/20)
      // this.setState({twentyRupee: Math.floor(remainingValue/20)})
      remainingValue = remainingValue%20
    }
    if(remainingValue >= 10){
      var tenruppeeNotes = Math.floor(remainingValue/10)
      // this.setState({tenRupee: Math.floor(remainingValue/10)})
      remainingValue = remainingValue%10
    }
    if(remainingValue >= 5){
      var fiveRuppeeNotes = Math.floor(remainingValue/ 5)
      // this.setState({fiveRupee: Math.floor(remainingValue/5)})
      remainingValue = remainingValue%5
    }

    if(remainingValue >= 2){
      var twoRuppeeNotes = Math.floor(remainingValue/2)
      // this.setState({twoRupee: Math.floor(remainingValue/2)})
      remainingValue = remainingValue%2
    }
    if(remainingValue >= 1){
      var oneRupeeNotes = Math.floor(remainingValue/1)
      // this.setState({oneRupee: Math.floor(remainingValue/1)})
      remainingValue = remainingValue%1
    }

    return {twoThousandNotes, fiveHundredNotes, twoHundredNotes, hundredNotes, fiftyNotes, twentyRupeesNotes, tenruppeeNotes, fiveRuppeeNotes, twoRuppeeNotes, oneRupeeNotes}
  }

  render() {
    // const {amount, oneRupee , twoRupee , fiveRupee , tenRupee , twentyRupee , fiftyRupee , hundredRupee , twohundredRupee , fivehundredRupee , thousandRupee} = this.state;
    const total = oneRupee + twoRupee + fiveRupee + tenRupee + twentyRupee + fiftyRupee + hundredRupee + twohundredRupee + fivehundredRupee + thousandRupee;
    return (
    <div className="App" style={{display: 'flex', background: 'lightgray', padding: 20}}>
        <div style={{background: 'white', padding: 10, marginRight: 20}}>
          <h1>Welcome to ATM</h1>
          <form>
            <label>
              <p style={{fontSize: 12, textAlign: 'left'}}>Enter the label {amount}</p>
              <input type="number" onChange={this.handleChange} value={amount} style={{width: '100%', textAlign: 'right'}}/>
            </label>
            <div className="button" onClick={this.calculateNotes} style={{background: 'blue', color: 'white', width: '100%', marginTop: 10, textAlign: 'center', padding: 5}}>Get Money</div>
          </form>
        </div>
        <div style={{display: 'flex', flexDirection: "column"}}>
          {/*<Child/>*/}
          <h2>You will get the follwing amount</h2>
          <div style={{display: 'flex', justifyContent: 'space-between'}}>
            <span>{oneRupee} notes Rs 1</span>
            <span>{twoRupee} notes Rs 2</span>
          </div>
          <div style={{display: 'flex', justifyContent: 'space-between'}}>
            <span>{fiveRupee} notes Rs 5</span>
            <span>{tenRupee} notes Rs 10</span>
          </div>
          <div style={{display: 'flex', justifyContent: 'space-between'}}>
            <span>{twentyRupee} notes Rs 20</span>
            <span>{fiftyRupee} notes Rs 50</span>
          </div>
          <div style={{display: 'flex', justifyContent: 'space-between'}}>
            <span>{hundredRupee} notes Rs 100</span>
            <span>{twohundredRupee} notes Rs 200</span>
          </div>
          <div style={{display: 'flex', justifyContent: 'space-between'}}>
            <span>{fivehundredRupee} notes Rs 500</span>
            <span>{thousandRupee} notes Rs 2000</span>
          </div>
          <div style={{marginTop: 10}}>Total notes dispensed: {total}</div>
        </div>

    </div>
  );
    }
}

export default App;
