import React, { useState, useEffect } from 'react';
import ItemGrid from './ItemGrid'
import GridView from './GridView'
import {itemData} from './data'

const Board = () => {
  const [cloneState, setCloneState] = useState()

  const handleState = (value: any) => {
    // let gridValues = window.localStorage.getItem('gridView') || ''
    // JSON.parse(gridValues).push(value)
    // window.localStorage.setItem('gridValue', JSON.stringify(gridValues))
    let addedItem = cloneState;
    addedItem.push(value)
    setCloneState(addedItem)
    console.log(cloneState)
  }

  useEffect(()=> {},[cloneState])

  return <div style={{display: 'flex', alignItems: 'center'}}>
    <ItemGrid data={itemData} clickedItem={handleState}/>
    <GridView items={cloneState ? new Array(9) : cloneState}/>
  </div>
}

export default Board
