import React from 'react';
import { ItemType } from './types';

export interface ItemGridProp{
  data: ItemType[]
  clickedItem: (value: any) => {}
}

export interface GridItemProps extends ItemType{
  clickedItem: (value: number) => {}
}

export const GridItem = (props: GridItemProps) => (
  <img onClick={() => {console.log(props.id);props.clickedItem(props.id)}} 
      key={props.id} 
      alt={props.image} 
      style={{width: '25%'}} 
      src={props.image}/>
)
const ItemGrid = (props: ItemGridProp) => {
  return <div style={{flex: 1}}>
    {props.data.map((item: ItemType) => 
      <GridItem id={item.id} image={item.image} clickedItem={props.clickedItem}/>)}
  </div>
}

export default ItemGrid
