import React from 'react';
import { GridItem } from './ItemGrid';

export interface GridViewProps{
  items: any[]
}

const styles: React.CSSProperties = {
  width:60,
  height: 60, 
  border: '1px solid lightblue', 
  textAlign: 'center'
}

const styleRow: React.CSSProperties ={
  display: 'flex',
  flexDirection: 'row'
}

const GridView = (props: GridViewProps) => {

  return <div style={{flex: 1, flexDirection: 'column'}}>
    <div style={styleRow}>{props.items.filter((item, index) => index>=2 && <div key={index} style={styles}>
      {item && item.id && <GridItem id={item.id} image={item.image} />}
      </div>)}
    </div>
    <div style={styleRow}>{props.items.filter((item, index) => index>=5 && <div key={index} style={styles}>
      {item && item.id  && <GridItem id={item.id} image={item.image} />}
      </div>)}
    </div>
    <div style={styleRow}>{props.items.filter((item, index) => index>=5 && <div key={index} style={styles}>
      {item && item.id &&<GridItem id={item.id} image={item.image} />}
      </div>)}
    </div>
  </div>
}

export default GridView
