import React, { useRef } from 'react';
import { ItemType } from './types';

export interface ItemGridProp{
  data: ItemType[]
  clickedItem: (value) => {}
}
const ItemGrid = (props: ItemGridProp) => {
  const imageRef = useRef(null)

  return <div style={{flex: 1}}>
    {props.data.map((item: ItemType) => <img ref={imageRef} onClick={(e) => {console.log(e),props.clickedItem(React.cloneElement(imageRef, {}))}} key={item.id} alt={item.image} style={{width: '25%'}} src={item.image}/>)}
  </div>
}

export default ItemGrid
